    rf_gfx_tex_coord2f((_rf_ctx->rec_tex_shapes.x + _rf_ctx->rec_tex_shapes.width)/_rf_ctx->tex_shapes.width, (_rf_ctx->rec_tex_shapes.y + _rf_ctx->rec_tex_shapes.height)/_rf_ctx->tex_shapes.height);
